INSERT INTO `tw_posts` VALUES ('420', '1', '2015-08-03 15:55:22', '2015-08-03 08:55:22', '<a href=\"http://trungtamgiasuonline.com/\" target=\"_blank\"><span style=\"color: #0000ff;\"> Trung tâm gia sư online</span> </a>được thành lập với sứ mệnh hỗ trợ giúp đỡ cùng quí phụ huynh làm cho trẻ vững kiến thức cơ bản và những kiến thức nâng cao từ đó sánh ngang với các cường quốc năm châu. Vì vây, trung tâm gia sư luôn mong muốn được sát cánh san sẻ trách nhiệm cùng phụ huynh  giúp những thế hệ trẻ tiến bộ hơn nữa\r\n\r\nĐể thuận lợi nhất cho phụ huynh khi kết nối với trung tâm tìm gia sư về dạy kèm tại nhà cho con em mình. Chúng tôi đã cố gắng đơn giản nhất những thủ tục cũng như là quá trình vận chuyển của phụ huynh qua trung tâm mà vẫn đảm bảo tìm được gia sư ưng ý đủ chất lượng để giúp bé tiến bộ trong thời gian nhanh nhất. Hãy lien hệ với trung tâm theo những cách sau:\r\n\r\n<span style=\"color: #ff0000;\">Cách 1: ĐIỆN THOẠI</span>\r\n\r\n[caption id=\"attachment_379\" align=\"aligncenter\" width=\"234\"]<a href=\"http://trungtamgiasuonline.com/wp-content/uploads/2015/07/điện-thoại-cổ.jpg\"><img class=\"wp-image-379 size-full\" src=\"http://trungtamgiasuonline.com/wp-content/uploads/2015/07/điện-thoại-cổ.jpg\" alt=\"điện thoại cổ\" width=\"234\" height=\"215\" /></a> <span style=\"color: #0000ff;\"><a href=\"http://trungtamgiasuonline.com/\" target=\"_blank\">Tìm Gia Sư</a></span>[/caption]\r\n\r\nPhụ huynh gọi điện trực tiếp với trung tâm qua hotline để được tư vấn trực tiếp:\r\n\r\n<span style=\"color: #0000ff;\">0902 896 096( hotline 1)</span>\r\n\r\n<span style=\"color: #0000ff;\">0913 880 955 ( hotline2)</span>\r\n\r\nHỗ trợ tư vấn <span style=\"color: #ff0000;\">24/7</span>\r\n\r\n<span style=\"color: #ff0000;\">CÁCH 2: ĐĂNG KÍ TÌM GIA SƯ TRÊN WEBSITE</span>\r\n\r\nPhụ huynh truy cập web chính thức của trung tâm:<span style=\"color: #0000ff;\"><a style=\"color: #0000ff;\" href=\"http://trungtamgiasuonline.com/\" target=\"_blank\"> Trungtamgiasuonline.com</a></span> vào mục <span style=\"color: #ff0000;\"><a style=\"color: #ff0000;\" href=\"http://trungtamgiasuonline.com/tim-kiem-gia-su\" target=\"_blank\">Tìm gia sư</a></span> để nhập thông tin yêu cầu. Chúng tôi sẽ liên hệ với phụ huynh trong vòng 2h-4h từ lúc nhận thông tin đăng kí của quí phụ huynh và lựa chọn <a href=\"http://trungtamgiasuonline.com/\" target=\"_blank\"><span style=\"color: #0000ff;\">gia sư</span> </a>phù hợp với yêu cầu của phụ huynh đã đưa ra.\r\n\r\n<span style=\"color: #ff0000;\">CÁCH 3:</span>\r\n\r\n<span style=\"color: #ff0000;\">ĐẾN TRỰC TIẾP VĂN PHÒNG TRUNG TÂM</span>\r\n\r\n[caption id=\"attachment_380\" align=\"aligncenter\" width=\"530\"]<a href=\"http://trungtamgiasuonline.com/wp-content/uploads/2015/07/tư-vấn.jpg\"><img class=\"wp-image-380 size-full\" src=\"http://trungtamgiasuonline.com/wp-content/uploads/2015/07/tư-vấn.jpg\" alt=\"Tư vấn tìm gia sư\" width=\"530\" height=\"378\" /></a> <span style=\"color: #0000ff;\"><a style=\"color: #0000ff;\" href=\"http://trungtamgiasuonline.com/\" target=\"_blank\">Tư vấn tìm gia sư</a></span>[/caption]\r\n\r\n<span style=\"color: #0000ff;\"> </span>\r\n\r\n<span style=\"color: #ff0000;\">235B/A8/4 NGUYỄN CƯ TRINH Q1</span>\r\n\r\n<span style=\"color: #ff0000;\">15 Huỳnh Thúc Kháng F. Hiệp Phú Q.9</span>\r\n\r\n<a href=\"http://trungtamgiasuonline.com/\" target=\"_blank\"><span style=\"color: #0000ff;\">Trung tâm gia sư online</span></a> chúng tôi luôn đảm bảo những quyền lợi sau cho phụ huynh:\r\n<ul>\r\n	<li><span style=\"color: #ff0000;\">Giới thiệu gia sư uy tín đúng yêu cầu với mức học phí hợp lí nhất</span></li>\r\n	<li><span style=\"color: #ff0000;\">Miễn phí học thử 3 buổi</span></li>\r\n</ul>\r\nGỌI NGAY VỚI CHÚNG TÔI ĐỂ ĐƯỢC TƯ VẤN <a href=\"http://trungtamgiasuonline.com/\" target=\"_blank\"><span style=\"color: #0000ff;\">TÌM GIA SƯ </span></a><span style=\"color: #ff0000;\">MIỄN PHÍ!</span>\r\n\r\n<span style=\"color: #0000ff;\">0913 880 955 VP Q1</span>\r\n\r\n<span style=\"color: #0000ff;\">0902 896 096 VP Q9</span>\r\n\r\nChúng tôi xin gửi lời tri ân lời cảm ơn tới toàn thể quí phụ huynh đã tin tưởng và để chúng tôi đồng hành trong suốt thời gian qua. Chúng tôi hi vọng sẽ được sự tín nhiệm nhiều hơn từ quí phụ huynh trong thời gian tới đó cũng là động lực để chúng tôi cố gắng hoàn thiện và phục vụ nhiều hơn nữa giúp học sinh thành đạt trong tương lai.', 'Quy trình tìm gia sư', '', 'inherit', 'open', 'open', '', '377-revision-v1', '', '', '2015-08-03 15:55:22', '2015-08-03 08:55:22', '', '377', 'http://trungtamgiasuonline.com/377-revision-v1.html', '0', 'revision', '', '0');
INSERT INTO `tw_posts` VALUES ('421', '1', '2015-08-03 16:43:34', '2015-08-03 09:43:34', 'Với mong muốn hợp tác lâu dài cùng quí thầy cô các bạn sinh viên có nhu cầu <span style=\"color: #0000ff;\"><a style=\"color: #0000ff;\" href=\"http://trungtamgiasuonline.com/dang-ky-lam-gia-su\" target=\"_blank\">làm gia sư</a></span>. <span style=\"color: #0000ff;\"><a style=\"color: #0000ff;\" href=\"http://trungtamgiasuonline.com/\" target=\"_blank\">Trung tâm gia sư</a></span> chúng tôi xin giời thiệu thủ tục qui trình nhận lớp như sau:\r\n\r\n<span style=\"color: #0000ff;\">Điều kiện nhận lớp:</span>\r\n\r\nTất cả giáo viên, các bạn sinh viên có nhu cầu dạy kèm tại nhà muốn nhận lớp phải thỏa mãn nhưng yêu cầu:\r\n\r\n+ Đủ điều kiện của lớp muốn nhận( kiểm tra kiến thức)\r\n\r\n+ Phù hợp với năng lục bản thân\r\n\r\nSau khi gia sư thấy lớp phù hợp, quí thầy cô, các bạn sinh viên liên hệ trung tâm để đặt lớp và hẹn thời gian tới trung tâm làm thủ tục nhận lớp( Đóng phí+ kí hợp đồng + Nhận giấy giới thiệu)\r\n\r\n<span style=\"color: #ff0000;\">Lệ phí:</span> <span style=\"color: #0000ff;\">Giáo viên:</span> 30% lương tháng đầu\r\n\r\n<span style=\"color: #0000ff;\">Sinh viên:</span> 35%  lương tháng đầu\r\n\r\nTrung tâm có trách nhiệm hỗ trợ liên lạc hẹn ngày giờ gặp để gia sư phụ huynh khi gặp phụ huynh <a href=\"http://trungtamgiasuonline.com/\" target=\"_blank\"><span style=\"color: #0000ff;\">gia sư </span></a>buộc phải trình giấy giời thiệu của trung tâm cung cấp cho phụ huynh.\r\n\r\nTrong trường hợp gia sư nhận lớp có xảy ra vvaans đề như: không gặp được phụ huynh, thông tin lớp không đúng như trung tâm đăng, số lượng h/s tăng , thay đổi thời gian như hợp đòng kí với trung tâm…dẫn đến việc chưa nhận được lớp thì ngay khi giáo viên, sinh viên rời khỏi nhà phụ huynh <span style=\"color: #0000ff;\">gia sư</span> phải báo gấp về trung tâm để trung tâm xác minh và giải quyết.\r\n\r\nTrung tâm sẽ chịu trách nhiệm hoàn phí 100% cho quí thầy cố, các bạn sinh viên nếu không nhận được lớp, không được nhận trong quá trình dạy thử. Khi đến nhận phí quí thầy, cô, các bạn sinh viên cần mang biên lai, giấy giới thiệu để đối chiếu thông tin.\r\n\r\nTrường hợp giáo viên, sinh viên không nhận được lớp do phía phụ huynh gây ra thì giáo viên có thể nhận lại phí 100% hoặc đổi lớp mới tương đương nếu có nhu cầu.\r\n\r\nKhi nhận lớp gia sư nên xem xét kỹ về đường đi, thời gian, năng lực phù hợp…trung tâm sẽ không hoàn phí nếu gia sư tự động bỏ lớp, thương lượng tăng lương, chuyển lớp cho người khác day làm mất lớp…\r\n\r\nVới tinh thần tất cả vì sự tiến bộ của học sinh sự hài long của phụ huynh và đảm bảo quyền lợi của giáo viên – sinh viên chúng tôi luôn mong muốn nhận được những chia sẻ góp ý xây dựng từ phía các  thầy, cô các bạn sinh viên.\r\n\r\n<span style=\"color: #0000ff;\">Trân trọng hợp tác!</span>', 'Lệ phí nhận lớp', '', 'publish', 'open', 'open', '', 'le-phi-nhan-lop', '', '', '2015-08-03 16:43:34', '2015-08-03 09:43:34', '', '0', 'http://trungtamgiasuonline.com/?p=421', '0', 'post', '', '0');
